import React, { useEffect, useState } from 'react';

function Header({history}) {
    // const check = false;
    const [isBlocking, setISBlocking] = useState(false);

    const onClick = () => {
        history.replace("/");
        // check = true;
        // setCheck(true);
        console.log('clicked');
        setISBlocking(true);
    };

    useEffect(() => {
        console.log(history);
        const unblock = history.block(() => {
            if (isBlocking) {
                return window.confirm("시작 화면으로 가실건가요?");
            }
            return true;
        });
        return () => unblock();
    }, [onClick]);

    return (
        <div>
            <div className="Header">
                <div onClick={onClick}>투자 유니버스</div>
            </div>
        </div>
    );
}

export default Header;
