  let AccumulatePeriod = RetireAge - PresentAge; //ì ë¦½ê¸°ê°„ (í˜„ì¬ ~ ì€í‡´ ì „)
  let exceptP = 0; //SUM - Compund(P) : ë…„ ì ë¦½ ê¸ˆì•¡ êµ¬í•˜ê¸° ìœ„í•¨

  console.log("******ì€í‡´ ê³„ì‚°ê¸°******");
