            />
          </FormControl>
        </Box>
        <span>ì‚´ì´ê³ , ì€í‡´ëŠ”</span>
        <Box sx={{ display: "flex" }}>
          <FormControl variant="standard">
            <Input
              id="RetireAge"
              value={RetireAge}
              onChange={handleRetireAge}
              inputProps={{ pattern: "[0-9]*" }}
              type="number"
              size="small"
              sx={{ width: "48px", textAlign: "right", ml: "0.5rem" }}
            />
          </FormControl>
        </Box>
        <span>ì‚´ì— í•  ê²ƒ ê°™ì•„ìš”.</span>
        <br />
        <span>ì€í‡´ í›„ì—ëŠ” </span>
        <Box sx={{ display: "flex" }}>
          <FormControl variant="standard">
            <Input
              id="ReceivePeriod"
              value={ReceivePeriod}
              onChange={handleReceivePeriod}
              inputProps={{ pattern: "[0-9]*" }}
              type="number"
              size="small"
              sx={{ width: "48px", ml: "0.5rem" }}
            />
          </FormControl>
        </Box>
        <span>ë…„ê°„ </span>
        <Box sx={{ display: "flex" }}>
          <FormControl variant="standard">
            <Input
              id="MonthExpense"
              value={MonthExpense}
              onChange={handleMonthExpense}
              inputProps={{ pattern: "[0-9]*" }}
              type="number"
              size="small"
              sx={{ width: "64px", textAlign: "right", ml: "0.5rem" }}
            />
          </FormControl>
        </Box>
        <span>ë§Œì›ì˜ ìƒí™œë¹„ë¡œ</span>
        <span>ì¶”ê°€ ìˆ˜ì… ì—†ì´ ì‚´ê±°ì—ìš”. </span>
        <br />
        <span>(ì´ˆê¸° íˆ¬ìê¸ˆì€ </span>
        <Box sx={{ display: "flex" }}>
          <FormControl variant="standard">
            <Input
              id="P"
              value={P}
              onChange={handleP}
              inputProps={{ pattern: "[0-9]*" }}
              type="number"
              size="small"
              sx={{ width: "64px", textAlign: "right", ml: "0.5rem" }}
            />
          </FormControl>
        </Box>
        <span>ë§Œì›ì´ì—ìš”)</span>
        <div
          style={{
            backgroundColor: "#F4F3F4",
            width: "100%",
            margin: "1rem 0",
            padding: "0.5rem 1.2rem",
            borderRadius: "0.8rem",
          }}
        >
          <span style={{ marginTop: "0.8rem" }}>
            ì€í‡´ ëª©í‘œë¥¼ ì´ë£¨ê¸° ìœ„í•´ ë§¤ë…„ <b>{ROUNDTWO(PMT)}</b>ë§Œì›ì„ ë„£ì–´ì•¼í•´ìš”!
          </span>
        </div>
      </div>

      <ComposedChart
        width={400}
        height={250}
        margin={{ top: 20, bottom: 20 }}
        data={data}
      >
        <XAxis dataKey="year" tick={{ fontSize: 10 }} padding={{ right: 20 }} />
        <YAxis
          unit={"ë§Œì›"}
          tick={{ fontSize: 10 }}
          padding={{ bottom: 10 }}
          domain={["auto", "auto"]}
        />
        <Tooltip />
        {/* <Legend tick={{ fontSize: 10 }} /> */}
        <CartesianGrid stroke="#f4f4f4" />
        <Line
          unit={"ë§Œì›"}
          type="monotone"
          dataKey="ì˜ˆìƒìˆ˜ìµ"
          stroke="#1D1A82"
        />
      </ComposedChart>
    </div>
  );
}

export default Retirement;
