                <h3 className='page'>{ QnA_5.page }</h3> 
                <h1 className='question'>{ QnA_5.Question }</h1>
                <div className={'ell-body'}>
                    {QnA_5.Answers.map(answer => (
                        <MulSelection key= {answer.id} Answer={answer} />
                    ))}
                </div>
                <div style={{paddingTop: '0.5rem'}}></div>
