//단리
function Simple({P, r, t}) {
  return P + (r / 100) * P * t;
}

//복리
function Compound({P, r, t}) {
  return P * (r / 100 + 1) ** t;
}

//적립식 복리
function Contribute({PMT, r, t}) {
  if (r !== 0) {
    return (PMT * ((1 + r / 100) ** t - 1)) / (r / 100);
  } else {
    //이자율이 0인 경우, 계산식 분모에 0이 들어가면서 값이 무한대로 수렴 -> 예외처리
    return PMT * t;
  }
}

//현재가치
function PV({FV, t}) {
  const inflationRate = 2;
  return FV / (1 + inflationRate / 100) ** t;
}

export default { Simple, Compound, Contribute, PV };
