import React, { useState } from "react";
import Radio from "@mui/material/Radio";
import RadioGroup from "@mui/material/RadioGroup";
import FormControlLabel from "@mui/material/FormControlLabel";
import FormControl from "@mui/material/FormControl";
import FormLabel from "@mui/material/FormLabel";
import {
  TextField,
  Typography,
  Box,
  InputAdornment,
  Input,
} from "@mui/material";

function GBIGraph({ Min, Max, Pro }) {
  //거치식 GBI
  function formula_LumpSum(T, r, t) {
    return T / (r / 100 + 1) ** t;
  }
  //적립식 GBI
  function formula_Mutual(T, r, t) {
    return (T * (r / 100) + 1) / (1 + r / 100) ** t;
  }

  const [Method, setMethod] = useState("LumpSum");
  const [Period, setPeriod] = useState(1);
  const [Money, setMoney] = useState(100);

  const handleRadio = (event) => {
    setMethod(event.target.value);
  };
  const handlePeriod = (event) => {
    setPeriod(event.target.value);
  };
  const handleMoney = (event) => {
    setMoney(event.target.value);
  };

  return (
    <div>
      {/* 라디오 버튼 */}
        <Box sx={{ display: "flex" }}>
          <FormControl variant="standard">
            <Select
              labelId="year-select-label"
              id="year-select"
              value={Period}
              label="년"
              onChange={PER_handleChange}
              defaultValue={0}
            >
              {Year.map((Value, index) => {
                return (
                  <MenuItem key={index} value={Value}>
                    {Value}년
                  </MenuItem>
                );
              })}
            </Select>
          </FormControl>
        </Box>
        <FormLabel id="period">몇년간 투자할 건가요?</FormLabel>
        <TextField
          id="period"
          variant="standard"
          size="small"
          type="number"
          InputLabelProps={{
            shrink: true,
          }}
        ></TextField>
        <span>동안</span>
        <Box sx={{ display: "flex" }}>
          <FormControl variant="standard" sx={{ m: 1 }}>
            <Input
              id="money"
              value={Money}
              onChange={handleMoney}
              endAdornment={
                <InputAdornment position="end">만원</InputAdornment>
              }
              size="small"
              sx={{ width: "100px" }}
            />
          </FormControl>
        </Box>
    </div>
  );
}

export default GBIGraph;
