import React, { useState } from "react";
import Radio from "@mui/material/Radio";
import RadioGroup from "@mui/material/RadioGroup";
import FormControlLabel from "@mui/material/FormControlLabel";
import FormControl from "@mui/material/FormControl";
import FormLabel from "@mui/material/FormLabel";
import { TextField, Typography } from "@mui/material";

function GBIGraph({ Min, Max, Pro }) {
  //거치식 GBI
  function formula_LumpSum(T, r, t) {
    return T / (r / 100 + 1) ** t;
  }
  //적립식 GBI
  function formula_Mutual(T, r, t) {
    return (T * (r / 100) + 1) / (1 + r / 100) ** t;
  }

  const [Method, setMethod] = useState("LumpSum");

  const handleRadio = (event) => {
    setMethod(event.target.value);
  };

  return (
    <div>
      {/* 라디오 버튼 */}
      <FormControl>
        <FormLabel id="method">어떻게 투자할 건가요?</FormLabel>
        <RadioGroup
          row
          aria-labelledby="method"
          name="method"
          value={Method}
          onChange={handleRadio}
        >
          <FormControlLabel
            value="LumpSum"
            control={<Radio size="small" />}
            label={<Typography sx={{ fontSize: 14 }}>한번에 투자</Typography>}
          />
          <FormControlLabel
            value="Mutual"
            control={<Radio size="small" />}
            label={<Typography sx={{ fontSize: 14 }}>적립해 투자</Typography>}
          />
        </RadioGroup>
        <FormLabel id="period">몇년간 투자할 건가요?</FormLabel>
        <TextField id="period" variant="outlined"></TextField>
        <FormLabel id="money">목표하는 금액은 얼마인가요?</FormLabel>
      </FormControl>
    </div>
  );
}

export default GBIGraph;
