  console.log("은퇴 자금 수령 기간:", ReceivePeriod);
  console.log("월 생활비:", MonthExpense);
  console.log("목표 금액:", GoalMoney);
  console.log("년 적립금액:", PMT);

  const handlePresentAge = (event) => { getPresentAge(event.target.value); };
  const handleRetireAge = (event) => { getRetireAge(event.target.value); };  
  const handleReceivePeriod = (event) => { getReceivePeriod(event.target.value); };  
  const handleMonthExpense = (event) => { getMonthExpense(event.target.value); };

  //Goal 금액 정하기
  let RetireArray = [{year: AccumulateYears+ReceivePeriod+1, 예상수익: 0}] // 마지막 값 세팅

  const numbers = [1, 2, 3];

  const tmp = numbers.map((number, index, source) => {
