import React from "react";
import FormControl from "@mui/material/FormControl";
import { Box, InputAdornment, Input, MenuItem, Select } from "@mui/material";
import {
  ComposedChart,
  XAxis,
  YAxis,
  Tooltip,
  Legend,
  CartesianGrid,
  Area,
  Line,
} from "recharts";
import { CompoundContribution } from "../calculate/Calculator";
import { ROUNDTWO, LUMPSUM, MUTUAL } from "../calculate/Formula";
import {FV} from "../calculate/Formula"

function Retirement({ Pro, Age }) {
  console.log(Age);
  const [PresentAge, getPresentAge] = useState(Age);
  const [RetireAge, getRetireAge] = useState(70);
  const [PMT, setPMT] = useState(0);

  return (
    <div style={{ marginTop: "1rem" }}>
      <h3 style={{ margin: "0.3rem 0" }}>📈 은퇴 계산기</h3>
      <div className="Description" style={{ marginTop: "1rem" }}>
        <span>으로</span>
        <Box sx={{ display: "flex" }}>
          <FormControl variant="standard">
            <Input
              id="period"
              value={Period}
              onChange={handlePeriod}
              endAdornment={<InputAdornment position="end">년</InputAdornment>}
              size="small"
              sx={{ width: "48px", ml: "0.5rem" }}
            />
          </FormControl>
        </Box>
        <span>동안</span>
        <Box sx={{ display: "flex" }}>
          <FormControl variant="standard">
            <Input
              id="money"
              value={T}
              onChange={handleT}
              endAdornment={
                <InputAdornment position="end">만원</InputAdornment>
              }
              inputProps={{ pattern: "[0-9]*" }}
              //   type="number"
              size="small"
              sx={{ width: "100px", textAlign: "right", ml: "0.5rem" }}
            />
          </FormControl>
        </Box>
        <span>을 모으기 위해...</span>
        {Method === "LumpSum" && (
          <span style={{ marginTop: "0.8rem" }}>
            초기 투자 금액은 <b>{ROUNDTWO(P)}</b>만원이에요!
          </span>
        )}
        {Method === "Mutual" && (
          <span style={{ marginTop: "0.8rem" }}>
            매년 <b>{ROUNDTWO(PMT)}</b>만원을 넣어야해요!
          </span>
        )}
      </div>

      <ComposedChart
        width={400}
        height={250}
        margin={{ top: 20, bottom: 20 }}
        data={data}
      >
        <XAxis dataKey="year" tick={{ fontSize: 10 }} padding={{ right: 20 }} />
        <YAxis
          unit={"만원"}
          tick={{ fontSize: 10 }}
          padding={{ bottom: 10 }}
          domain={["auto", "auto"]}
        />
        <Tooltip />
        <Legend tick={{ fontSize: 10 }} />
        <CartesianGrid stroke="#f4f4f4" />
        <Area
          unit={"만원"}
          type="monotone"
          dataKey="예상수익범위"
          fill="#FFB950"
          stroke="#FFB950"
        />
        <Line
          unit={"만원"}
          type="monotone"
          dataKey="예상수익"
          stroke="#1D1A82"
        />
        <Line
          unit={"만원"}
          type="monotone"
          dataKey="현재가치"
          stroke="#B45CCA"
        />
      </ComposedChart>
    </div>
  );
}

export default Retirement;
