import React from 'react';
import '../App.css';
import Header from '../component/Header';
import RESULT from '../content/RESULT';
import TYPE from '../content/TYPE';
import Final_TYPE from '../content/FinalTYPE';
import NextButton from '../component/NextButton';
import QNA from '../content/QNA';

const Result = RESULT;
const Type = TYPE;
const Final_Type = Final_TYPE;

function Final_Result(props) {
  const Res = props.location.state.Res;

  //Set Period Value - Q1
  const Period = Res[1];
  //Set Purpose Value - Q2
  const Purpose = QNA[1].Answers.find(function(data) {
    return data.id === Res[2]
  });
  //Set Tolerance Value - Q3, Q4
  const Tol1 = QNA[2].Answers.find(function(data) {
    return data.id === Res[3]
  });
  const Tol2 = QNA[3].Answers.find(function(data) {
    return data.id === Res[4]
  });
  const Tolerance = (Tol1.Value + Tol2.Value)/2; //Sum of Q3, Q4
  //Set Literacy Value - Q5 (Sum of All Response)
  let Literacy = 0; //-> Literacy value
  let tmp = []; //-> 값 보관함 
  for(let i = 0; i <Res[5].length; i++){
    tmp[i] = QNA[4].Answers.find(function(data){
      return data.id === Res[5][i]
    });
    Literacy = Literacy + tmp[i].Value;
    // console.log('Literacy: ', Literacy, "Value: ", tmp[i].Value);
  };
  //Set Experience Value - Q6
  const Experience = QNA[5].Answers.find(function(data) {
    return data.id === Res[6]
  });

  console.log('Period: ', Period);
  console.log('Purpose: ', Purpose);
  console.log('Tolerance: ', Tolerance);
  console.log('Literacy: ', Literacy);
  console.log('Experience: ', Experience);

  const a_idx = [-1, -1, -1, -1, -1]; //투자기간, 투자목적, 위험감내도, 금융이해도, 투자경험
  let TypeIdx = -1; // 투자유형

  let Score = 0;

  function SetPeriod() { //투자 기간 Index 세팅
    let idx = -1;
    // console.log('투자 기간 응답: ', Period);
    if(Period === 5) {
      idx = 0;
      console.log('Period Index: ', idx);
    } else if (Period === 4) { 
      idx = 1;
      console.log('Period Index: ', idx);
    } else if (Period === 3) {
      idx = 2;
      console.log('Period Index: ', idx);
    } else if (Period === 2) {
      idx = 3;
      console.log('Period Index: ', idx);
    } else if (Period === 1) {
      idx = 4;
      console.log('Period Index: ', idx);
    } else {
      console.log('No result');
    }
    return idx;
  }
  function SetPurpose() { //투자 목적 Index 세팅
    let idx = -1;
    console.log('투자 목적 응답: ', Purpose.Value);
    if(Purpose.Value >= 26) {
      idx = 0;
      console.log('Purpose Index: ', idx);
    } else if (Purpose.Value >= 16 && Purpose.Value < 26) { 
      idx = 1;
      console.log('Purpose Index: ', idx);
    } else if (Purpose.Value >= 6 && Purpose.Value < 16) {
      idx = 2;
      console.log('Purpose Index: ', idx);
    } else if (Purpose.Value < 6) {
      idx = 3;
      console.log('Purpose Index: ', idx);
    } else {
      console.log('No result');
    }
    return idx;
  }
  function SetTolerance() { //위험 감내도 Index 세팅
    let idx = -1;
    console.log('위험 감내도 응답: ', Tolerance);
    if(Tolerance >= 26) {
      idx = 0;
      console.log('Tolerance Index: ', idx);
    } else if (Tolerance >= 16 && Tolerance < 26) { 
      idx = 1;
      console.log('Tolerance Index: ', idx);
    } else if (Tolerance >= 6 && Tolerance <= 16) {
      idx = 2;
      console.log('Tolerance Index: ', idx);
    } else if (Tolerance < 6) {
      idx = 3;
      console.log('Tolerance Index: ', idx);
    } else {
      console.log('No result');
    }
    return idx;
  }
  function SetLiteracy() { //위험 감내도 Index 세팅
    let idx = -1;
    console.log('금융이해도 응답: ', Literacy);
    if(Literacy >= 8) {
      idx = 0;
      console.log('Literacy Index: ', idx);
    } else if (Literacy >= 6 && Literacy < 8) { 
      idx = 1;
      console.log('Literacy Index: ', idx);
    } else if (Literacy >= 3 && Literacy < 6) {
      idx = 2;
      console.log('Literacy Index: ', idx);
    } else if (Literacy < 3) {
      idx = 3;
      console.log('Literacy Index: ', idx);
    } else {
      console.log('No result');
    }
    return idx;
  }
  function SetExperience() { //투자 경험 Index 세팅
    let idx = -1;
    console.log('투자 경험 응답: ', Experience.Value);
    if(Experience.Value >= 31) {
      idx = 0;
      console.log('Experience Index: ', idx);
    } else if (Experience.Value >= 21 && Experience.Value < 31) { 
      idx = 1;
      console.log('Experience Index: ', idx);
    } else if (Experience.Value >= 11 && Experience.Value < 21) {
      idx = 2;
      console.log('Experience Index: ', idx);
    } else if (Experience.Value < 11) {
      idx = 3;
      console.log('Experience Index: ', idx);
    } else {
      console.log('No result');
    }
    return idx;
  }

  a_idx[0] = SetPeriod();
  a_idx[1] = SetPurpose();
  a_idx[2] = SetTolerance();
  a_idx[3] = SetLiteracy();
  a_idx[4] = SetExperience();

  //투자 성향 점수 계산
  Score = (Result.투자목적[a_idx[1]].Value + Result.위험감내수준[a_idx[2]].Value + Result.금융이해도[a_idx[3]].Value + Result.투자경험[a_idx[4]].Value);

  function SetType() { //투자 성향 캐릭터 Index 세팅
    let idx=-1;
    console.log('투자 성향 점수: ', Score);
    if(Score >= 15) {
      idx=0;
      console.log('캐릭터 Index: ', idx);
    } else if (Score >= 13 && Score < 15) { 
      idx=1;
      console.log('캐릭터 Index: ', idx);
    } else if (Score >= 11 && Score < 13) {
      idx=2;
      console.log('캐릭터 Index: ', idx);
    } else if (Score >= 7 && Score < 11) {
      idx=3;
      console.log('캐릭터 Index: ', idx);
    } else if (Score < 7) {
      idx = 4;
      console.log('Experience Index: ', idx);
    } else {
      console.log('No result');
    }
    return idx;
  }
  TypeIdx = SetType();

  return (
