import React, { useState } from 'react';
import Header from '../component/Header';
import NextButton from '../component/NextButton';
import QNA from '../content/QNA'

const QnA = QNA[2];

function Q3(props) {
    // const Period = props.location.state.Period;
    // const Purpose = props.location.state.Purpose;
    const Answers = props.location.state.Answers;
    console.log(Answers)

    const [Tolerance, getTolerance] = useState('Not Selected!');
    const [Sel, setSel] = useState(false);

    function SelButton({Answer}) {

        const value = Answer.Value;
        const answer = Answer.Answer;

        const onClick = () => {
            QnA.Answers.map(answer => (
                answer.State = false
            ))
            Answer.State = !Answer.State;
            console.log(answer);
            getTolerance(value);
            setSel(true);
        }

        return (
            <div>
                <button 
                    className={'sel-button'} 
                    value={value} onClick={ onClick } 
                    style={{backgroundColor: Answer.State ? '#1d1a82':'#F7F7F7', color: Answer.State && 'White'}}>
                    {answer}
                </button>
            </div>
        );
    }

    Answers[QNA.page] = Tolerance;

    return (
        <div className="App">
            <Header />
            <div className='Content'>
                <h3 className='page'>{ QnA.page }</h3> 
                <h1 className='question'>{ QnA.Question }</h1>
                {QnA.Answers.map(answer => (
                    <SelButton key= {answer.id} Answer={answer}/>
                ))}
                {Sel ?  <NextButton Path={"/q4"} Ans={Answers} Text={'Next'}/> : <button disabled className='next'>Next</button> }
            </div>
        </div>
    );
}

export default Q3;
