      idx = 4;
      console.log('Experience Index: ', idx);
    } else {
      console.log('No result');
    }
    return idx;
  }
  TypeIdx = SetType();

  return (
    <div className="App">
      <h1>당신의 캐릭터는 아마도...</h1>
      <div className={'Quest'}>
        <h2>Quest</h2>
        <p>{Result_1.투자기간[a_idx[0]].Quest}</p>
      </div>
      <div> 
        <div className={'Column'}> 
          <h2>Character Info</h2>
          <h3>{Type[TypeIdx].Char}</h3>
          <p>{Type[TypeIdx].Content}</p>
          <ul>투자 목적: {Result_1.투자목적[a_idx[1]].Name}, {Result_1.투자목적[a_idx[1]].Grade}</ul>
          <ul>위험 감내도: {Result_1.위험감내수준[a_idx[2]].Name}, {Result_1.위험감내수준[a_idx[2]].Grade}</ul>
          <ul>금융 이해도: {Result_1.금융이해도[a_idx[3]].Name}, {Result_1.금융이해도[a_idx[3]].Grade}</ul>
          <ul>투자 경험: {Result_1.투자경험[a_idx[4]].Name}, {Result_1.투자경험[a_idx[4]].Grade}</ul>
        </div>
        <div className={'Column'}> 
          <h2>What's In My Bag?</h2>
          <h3>{Result_1.금융이해도[a_idx[3]].Weapon}</h3>
          <p>공격력: {Result_1.금융이해도[a_idx[3]].Power}</p>
          <h3>{Result_1.위험감내수준[a_idx[2]].Shield}</h3>
          <p>방어력: {Result_1.위험감내수준[a_idx[2]].Power}</p>
        </div>
      </div>
    </div>
  );
}


