import React from "react";
import "../App.css";
import Header from "../component/Header";
import RESULT from "../content/RESULT";
import Final_TYPE from "../content/FinalTYPE";
import NextButton from "../component/NextButton";
import QNA from "../content/QNA";
import Graph from "../component/Graph";
import CircularBar from "../component/CircularBar";

const Result = RESULT;
const Final_Type = Final_TYPE;

function Final_Result(props) {
  const Res = props.location.state.Res;
  console.log(Res);

  function formula_Index(Score) {
    const Index =
      Score >= 100
        ? 4 - parseInt(99 / 25)
        : Score < 0
        ? 4 - parseInt(1 / 25)
        : 4 - parseInt(Score / 25);
    console.log("******formula_Index", Score, Index);

    return Index;
  }

  //Type: PERIOD, PURPOSE, TOLERANCE, LITERACY, EXPERIENCE
  function find_ResultData(Type, Index) {
    console.log("******find_ResultData", Type, Index);
    return RESULT[Type][RESULT[Type].findIndex((data) => data.Index === Index)];
  }

  function getScore(questionID) {
    const Tdata = ASCORE[ASCORE.findIndex((data) => data.id === questionID)];
    if (Tdata.Type === "Single") {
      return Tdata.Answer[
        Tdata.Answer.findIndex((d) => d.id === Res[questionID])
      ].Score;
    } else if (Tdata.Type === "Multi") {
      console.log(Tdata, Res[questionID]);
      let Score = 0;

      for (let i = 0; i < Res[questionID].length; i++) {
        Score =
          Score +
          Tdata.Answer[
            Tdata.Answer.findIndex((d) => d.id === Res[questionID][i])
          ].Score;
      }
      return Score;
    }
  }

  const PERIOD = find_ResultData("PERIOD", getScore(1));
  const PURPOSE = find_ResultData(
    "PURPOSE",
    formula_Index((getScore(2) + getScore(7)) / 2)
  );
  const TOLERANCE = find_ResultData(
    "TOLERANCE",
    formula_Index(getScore(3) + getScore(4) + getScore(8))
  );
  const LITERACY = find_ResultData("LITERACY", formula_Index(getScore(5) + getScore(10) + getScore(11)));
    const EXPERIENCE = find_ResultData(
      "EXPERIENCE",
      formula_Index((getScore(6) + getScore(13))/2)
    );
    const AGE = find_ResultData("AGE", getScore(14));
    const INCOME = find_ResultData("INCOME", getScore(15));
    const PROPERTY = find_ResultData("PROPERTY", getScore(17));

  //토탈 점수 계산
  const Score =
    PURPOSE.Value +
    TOLERANCE.Value +
    LITERACY.Value +
    EXPERIENCE.Value +
    PERIOD +
    AGE.Value +
    INCOME.Value +
    PROPERTY.Value;

  //투자 성향 캐릭터 Index 세팅
  function SetType() {
    console.log("Total Score is... ", Total_Score, "\nScore is... ", Score);
    const idx =
      Total_Score >= 31 && 16 <= Score <= 18
        ? 0
        : Total_Score >= 31 && 14 <= Score && Score <= 15
        ? 1
        : Total_Score >= 31 && 12 <= Score && Score <= 13
        ? 2
        : 23 <= Total_Score && Total_Score <= 30 && 14 <= Score && Score <= 18
        ? 3
        : 23 <= Total_Score && Total_Score <= 30 && 12 <= Score && Score <= 13
        ? 4
        : 23 <= Total_Score && Total_Score <= 30 && 7 <= Score && Score <= 11
        ? 5
        : 18 <= Total_Score && Total_Score <= 22 && 14 <= Score && Score <= 18
        ? 6
        : 18 <= Total_Score && Total_Score <= 22 && 12 <= Score && Score <= 13
        ? 7
        : 18 <= Total_Score && Total_Score <= 22 && 7 <= Score && Score <= 11
        ? 8
        : 6 <= Total_Score && Total_Score <= 17 && Score === 13
        ? 9
        : 6 <= Total_Score && Total_Score <= 17 && 11 <= Score && Score <= 12
        ? 10
        : 6 <= Total_Score && Total_Score <= 17 && 7 <= Score && Score <= 10
        ? 11
        : Total_Score <= 5
        ? 12
        : console.log("Error Type");
    return idx;
  }

  const Type = Final_Type[SetType()];

  const MIN = Type.Min;
  const MAX = Type.Max;
  const REV = Type.Revenue;

  return (
    <div className="App">
      <Header history={props.history} />
      <div className="Result">
        <div>
          <div>
            <div className="Quest">
              <div
                style={{
                  fontSize: "20px",
                  fontWeight: "700",
                  fontFamily: "DungGeunMo",
                }}
              >
                Quest
              </div>
              <div>{Res_Period.Quest}</div>
            </div>
            {/* <img className='TypeImage'></img> */}
            <div className="TypeImage"></div>
            <h1
              style={{
                color: "#fefefe",
                fontFamily: "DungGeunMo",
                padding: "0.5rem",
              }}
            >
              {Type.Char}
            </h1>
          </div>

          <div className="Explain">
            <h3 style={{ margin: "0", paddingBottom: "0.8rem" }}>
              😎 당신의 캐릭터는...
            </h3>
            <div className="CharInfo">
              {/* 첫번째 칼럼: 등급표 */}
              <div className="Table">
                <CircularBar Type="투자 목적" Grade={Res_Purpose.Grade} />
                <CircularBar Type="위험 감내도" Grade={Res_Tolerance.Grade} />
                <CircularBar Type="금융 이해도" Grade={Res_Literacy.Grade} />
                <CircularBar Type="투자 경험" Grade={Res_Experience.Grade} />
              </div>
              {/* 2번째 칼럼: 아이템 */}
              <div className="Bag">
                <div className="Items">
                  <div className="ItemImg">
                    <img
                      width={42}
                      height={42}
                      src={Res_Literacy.Img}
                      alt="무기 이미지"
                    />
                  </div>
                  <div
                    style={{
                      marginTop: "auto",
                      marginBottom: "auto",
                      marginLeft: "0.5rem",
                    }}
                  >
                    <b>{Res_Literacy.Weapon}</b> <br />
                    {Res_Literacy.Level}
                  </div>
                </div>
                <div className="Items">
                  <div className="ItemImg">
                    <img
                      width={42}
                      height={42}
                      src={Res_Tolerance.Img}
                      alt="방패 이미지"
                    />
                  </div>
                  <div
                    style={{
                      marginTop: "auto",
                      marginBottom: "auto",
                      marginLeft: "0.5rem",
                    }}
                  >
                    <b>{Res_Tolerance.Shield}</b> <br />
                    {Res_Tolerance.Level}
                  </div>
                </div>
              </div>
            </div>
            {/* 캐릭터 설명 */}
            <div>
              <p style={{ lineHeight: "1.5rem" }}>{Type.Content}</p>
            </div>
            {/* 예상 수익률 그래프 */}
            <div style={{ marginTop: "1rem" }}>
              <h3 style={{ margin: "0.3rem 0" }}>📈 예상 수익률 그래프</h3>
              <Graph P={Money} Min={MIN} Max={MAX} Rev={REV} />
            </div>
          </div>
        </div>
        <NextButton Path={"/"} Text={"테스트 다시하기"} />
        <div
          style={{
            fontSize: "0.8rem",
            color: "#A7A8A3",
            textAlign: "left",
            padding: "1rem",
          }}
        >
          - 칼과 방패는 각각 금융이해도와 위험감내도로 정해집니다. <br />
          - 현재 가치는 미래의 가치를 현재를 기준으로 환산한 금액입니다. <br />
          - 현재 가치는 물가상승률(2021년 기준 2%)을 반영해 계산됩니다.
          <br />
        </div>
      </div>
    </div>
  );
}

export default Final_Result;
