        {
            id:13,
            Answer: "코스피", 
            Value: 1
        },
        {
            id:14, 
            Answer: "반모", 
            Value: -1
        },
        {
            id:15,
            Answer: "IPO", 
            Value: 1
        }
    ]
}

function Q5(props) {
    const Period = props.location.state.Period.Period;
    const Purpose = props.location.state.Purpose.Purpose;
    const Tolerance = props.location.state.Tolerance.Tolerance;
    function SelButton({ answer, value, state }) {
        const onClick = () => {
          console.log(value);
          state = True;
          ;
        }
        return (
            <div>
                <button className={'sel-button'} value={value} onClick={ onClick }>{answer}</button>
            </div>
        );
      }

    const [Literacy, getLiteracy] = useState(0);

    return (
        <div>
            <p>Peroid: {Period}</p>
            <p>Purpose: {Purpose}</p>
            <p>Tolerance: {Tolerance}</p>

            <h3>{ QnA_5.page }</h3> 
            <h1>{ QnA_5.Question }</h1>
            {QnA_5.Answers.map(answer => (
                <SelButton key= {answer.id} answer= {answer.Answer} value= {answer.Value}/>
            ))}
            <h4>Answer: { Tolerance }</h4>

            <Link to={{
                pathname: "/q6",
                state: {
                Period: {Period},
                Purpose: {Purpose},
                Tolerance: {Tolerance},
                Literacy: {Literacy},
                Experience: 0
                }
            }}><NextButton/></Link>            
        </div>
    );
}

export default Q5;
