import React, { useState } from "react";
import FormControl from "@mui/material/FormControl";
import { Box, InputAdornment, Input, MenuItem, Select } from "@mui/material";
import {
  ComposedChart,
  XAxis,
  YAxis,
  Tooltip,
  Legend,
  CartesianGrid,
  Area,
  Line,
} from "recharts";
import { CompoundContribution } from "../calculate/Calculator";
import { ROUNDTWO, LUMPSUM, MUTUAL } from "../calculate/Formula";
import {FV} from "../calculate/Formula"

function Retirement({ Pro, Age }) {
  console.log(Age);
  const [PresentAge, getPresentAge] = useState(Age); //현재나이
  const [RetireAge, getRetireAge] = useState(70); //은퇴나이
  const [ReceivePeriod, getReceivePeriod] = useState(15); //수령기간
  const [MonthExpense, getMonthExpense] = useState(180); //월 노후 생활비
  const [Seed, getSeed] = useState(0); //초기 투자 금액

  const [PMT, setPMT] = useState(0); //년 적립 금액

  const handlePresentAge = (event) => {
    getPresentAge(event.target.value);
  };
  const handleRetireAge = (event) => {
    getRetireAge(event.target.value);
  };  
  const handleReceivePeriod = (event) => {
    getReceivePeriod(event.target.value);
  };  
  const handleMonthExpense = (event) => {
    getMonthExpense(event.target.value);
  };

  return (
    <div style={{ marginTop: "1rem" }}>
      <h3 style={{ margin: "0.3rem 0" }}>📈 은퇴 계산기</h3>
      <div className="Description" style={{ marginTop: "1rem" }}>
        <span>현재 나이는 </span>
        <Box sx={{ display: "flex" }}>
          <FormControl variant="standard">
            <Input
              id="PresentAge"
              value={PresentAge}
              onChange={handlePresentAge}
              endAdornment={<InputAdornment position="end">살</InputAdornment>}
              size="small"
              sx={{ width: "48px", ml: "0.5rem" }}
            />
          </FormControl>
        </Box>
        <span>이고, 은퇴는</span>
        <Box sx={{ display: "flex" }}>
          <FormControl variant="standard">
            <Input
              id="RetireAge"
              value={RetireAge}
              onChange={handleRetireAge}
              endAdornment={<InputAdornment position="end">살</InputAdornment>}
              inputProps={{ pattern: "[0-9]*" }}
              //   type="number"
              size="small"
              sx={{ width: "100px", textAlign: "right", ml: "0.5rem" }}
            />
          </FormControl>
        </Box>
        <span>에 할 것 같아요.</span>
        <br />
        <span>은퇴 후에는 </span>
        <Box sx={{ display: "flex" }}>
          <FormControl variant="standard">
            <Input
              id="ReceivePeriod"
              value={ReceivePeriod}
              onChange={handleReceivePeriod}
              endAdornment={<InputAdornment position="end">년</InputAdornment>}
              size="small"
              sx={{ width: "48px", ml: "0.5rem" }}
            />
          </FormControl>
        </Box>
        <span>간 </span>
        <Box sx={{ display: "flex" }}>
          <FormControl variant="standard">
            <Input
              id="MonthExpense"
              value={MonthExpense}
              onChange={handleMonthExpense}
              endAdornment={
                <InputAdornment position="end">만원</InputAdornment>
              }
              inputProps={{ pattern: "[0-9]*" }}
              //   type="number"
              size="small"
              sx={{ width: "100px", textAlign: "right", ml: "0.5rem" }}
            />
          </FormControl>
        </Box>
        <span>의 생활비로 추가 수입 없이 살거에요.</span>
        <span style={{ marginTop: "0.8rem" }}>
          {/* 초기 투자 금액은 <b>{ROUNDTWO(P)}</b>만원이에요! */}
        </span>
      </div>

      <ComposedChart
        width={400}
        height={250}
        margin={{ top: 20, bottom: 20 }}
        data={data}
      >
        <XAxis dataKey="year" tick={{ fontSize: 10 }} padding={{ right: 20 }} />
        <YAxis
          unit={"만원"}
          tick={{ fontSize: 10 }}
          padding={{ bottom: 10 }}
          domain={["auto", "auto"]}
        />
        <Tooltip />
        <Legend tick={{ fontSize: 10 }} />
        <CartesianGrid stroke="#f4f4f4" />
        <Area
          unit={"만원"}
          type="monotone"
          dataKey="예상수익범위"
          fill="#FFB950"
          stroke="#FFB950"
        />
        <Line
          unit={"만원"}
          type="monotone"
          dataKey="예상수익"
          stroke="#1D1A82"
        />
        <Line
          unit={"만원"}
          type="monotone"
          dataKey="현재가치"
          stroke="#B45CCA"
        />
      </ComposedChart>
    </div>
  );
}

export default Retirement;
