import React, { useState } from "react";
import FormControl from "@mui/material/FormControl";
import { Box, InputAdornment, Input, MenuItem, Select } from "@mui/material";
import {
  ComposedChart,
  XAxis,
  YAxis,
  Tooltip,
  Legend,
  CartesianGrid,
  Area,
  Line,
} from "recharts";
import { CompoundContribution } from "../calculate/Calculator";
import {ROUNDTWO, LUMPSUM, MUTUAL} from "../calculate/Formula";


function GBIGraph({ Min, Max, Pro }) {
  //calculate
  let P = 0;
  let PMT = 0;
  //input
  const [Method, setMethod] = useState("LumpSum");
  const [Period, setPeriod] = useState(3);
  const [T, setT] = useState(10000);

  const handleMethod = (event) => {
    setMethod(event.target.value);
  };
  const handlePeriod = (event) => {
    //   if(event.target)
    setPeriod(event.target.value);
  };
  const handleT = (event) => {
    setT(event.target.value);
  };
  const formatYAxis = (tickItem) => {
    console.log(tickItem);
    let Money = tickItem;

    const units = ["ë§Œì›", "ì–µì›"];
    let unitIndex = 0;

    while (Money >= 10000 && unitIndex < units.length - 1) {
      unitIndex += 1;
      Money /= 10000;
      Money = ROUNDTWO(Money);
    }
    return `${Money.toLocaleString()}${units[unitIndex]}`;
  };
  const formatTooltip = (value, name) => {
    const units = ["ë§Œì›", "ì–µì›"];

    if (name === "ì˜ˆìƒìˆ˜ìµë²”ìœ„") {
      let unitIndex0 = 0;
      let unitIndex1 = 0;
      let Money0 = value[0];
      let Money1 = value[1];
      while (Money0 >= 10000 && unitIndex0 < units.length - 1) {
        unitIndex0 += 1;
        Money0 /= 10000;
        Money0 = ROUNDTWO(Money0);
      }
      while (Money1 >= 10000 && unitIndex1 < units.length - 1) {
        unitIndex1 += 1;
        Money1 /= 10000;
        Money1 = ROUNDTWO(Money1);
      }
      return [
        `${Money0.toLocaleString()}${
          units[unitIndex0]
        } ~ ${Money1.toLocaleString()}${units[unitIndex1]}`,
        "ì˜ˆìƒìˆ˜ìµë³Œìœ„",
      ];
    } else {
      let Money = value;
      let unitIndex = 0;

      while (Money >= 10000 && unitIndex < units.length - 1) {
        unitIndex += 1;
        Money /= 10000;
        Money = ROUNDTWO(Money);
      }
      return `${Money.toLocaleString()}${units[unitIndex]}`;
    }
  };

  if (Method === "LumpSum") {
    P = LUMPSUM(T, Pro, Period);
    PMT = 0;
  } else if (Method === "Mutual") {
    P = 0;
    PMT = MUTUAL(T, Pro, Period);
  }

  //ë°ì´í„° ì„¸íŒ…
  const Year = Array.from({ length: parseInt(Period) + 1 }, (v, i) => i);

  const data = Year.map(function (t) {
    return CompoundContribution(P, PMT, t, Min, Max, Pro);
  });

  return (
    <div style={{ marginTop: "1rem" }}>
      <h3 style={{ margin: "0.3rem 0" }}>ğŸ“ˆ ë‹¨ê¸° GBI ê·¸ë˜í”„</h3>
      <div className="Description" style={{ marginTop: "1rem" }}>
        <Box sx={{ display: "flex" }}>
          <FormControl variant="standard">
            <Select
              labelId="year-select-label"
              id="year-select"
              value={Method}
              onChange={handleMethod}
              defaultValue={"ê±°ì¹˜ì‹"}
            >
              <MenuItem value="LumpSum">ê±°ì¹˜ì‹</MenuItem>
              <MenuItem value="Mutual">ì ë¦½ì‹</MenuItem>
            </Select>
          </FormControl>
        </Box>
        <span>ìœ¼ë¡œ</span>
        <Box sx={{ display: "flex" }}>
          <FormControl variant="standard">
            <Input
              id="period"
              value={Period}
              onChange={handlePeriod}
              endAdornment={<InputAdornment position="end">ë…„</InputAdornment>}
              size="small"
              sx={{ width: "48px", ml: "0.5rem" }}
            />
          </FormControl>
        </Box>
        <span>ë™ì•ˆ</span>
        <Box sx={{ display: "flex" }}>
          <FormControl variant="standard">
            <Input
              id="money"
              value={T}
              onChange={handleT}
              endAdornment={
                <InputAdornment position="end">ë§Œì›</InputAdornment>
              }
              inputProps={{ pattern: "[0-9]*" }}
              //   type="number"
              size="small"
              sx={{ width: "100px", textAlign: "right", ml: "0.5rem" }}
            />
          </FormControl>
        </Box>
        <span>ì„ ëª¨ìœ¼ê¸° ìœ„í•´...</span>
        <div
          style={{
            backgroundColor: "#F4F3F4",
            width: "100%",
            margin: "1rem 0",
            padding: "0.5rem 1.2rem",
            borderRadius: "0.8rem",
          }}
        >
          {Method === "LumpSum" && (
            <span style={{ marginTop: "0.8rem" }}>
              ì´ˆê¸° íˆ¬ì ê¸ˆì•¡ì€ <b>{ROUNDTWO(P)}</b>ë§Œì›ì´ì—ìš”!
            </span>
          )}
          {Method === "Mutual" && (
            <span style={{ marginTop: "0.8rem" }}>
              ë§¤ë…„ <b>{ROUNDTWO(PMT)}</b>ë§Œì›ì„ ë„£ì–´ì•¼í•´ìš”!
            </span>
          )}
        </div>
      </div>

      <ComposedChart
        width={400}
        height={250}
        margin={{ top: 20, bottom: 20 }}
        data={data}
      >
        <XAxis dataKey="year" tick={{ fontSize: 10 }} padding={{ right: 20 }} />
        <YAxis
          tick={{ fontSize: 10 }}
          padding={{ bottom: 10 }}
          domain={["auto", "auto"]}
          tickFormatter={formatYAxis}
        />
        <Tooltip formatter={formatTooltip} />
        <Legend tick={{ fontSize: 10 }} />
        <CartesianGrid stroke="#f4f4f4" />
        <Area
          type="monotone"
          dataKey="ì˜ˆìƒìˆ˜ìµë²”ìœ„"
          fill="#FFB950"
          stroke="#FFB950"
        />
        <Line
          type="monotone"
          dataKey="ì˜ˆìƒìˆ˜ìµ"
          stroke="#1D1A82"
        />
        <Line
          type="monotone"
          dataKey="í˜„ì¬ê°€ì¹˜"
          stroke="#B45CCA"
        />
      </ComposedChart>
    </div>
  );
}

export default GBIGraph;
