        },
        {
            id:15,
            Answer: "IPO", 
            Value: 1,
            State: false
        }
    ]
}

function Test(props) {
    const [Literacy, getLiteracy] = useState(0);

    function MulSelection({ Answer }) {
        const value = Answer.Value;
        const answer = Answer.Answer;
        const [Color, setColor] = useState('#eee');

        const onClick = (e) => {
            Answer.State = !Answer.State;
            // const state = Answer.State;
            Color === '#eee'? setColor('#eee'):setColor('#aaa');
            Answer.State === false ? getLiteracy( Literacy + value ):getLiteracy( Literacy - value ); 

            // if(Answer.State !==false){
            //   getLiteracy( Literacy + value );
            //   console.log('Literacy: ' ,Literacy, 'State: ' ,Answer.State);
            //   return ;
            // }
            // else{
            //     getLiteracy( Literacy - value );
            //     console.log('Literacy: ' ,Literacy, 'State: ' ,Answer.State);
            //   return ;
            // }
        };

        return (
            <div>
                {/* <input type='checkbox' id='box'/> */}
                {/* <label for='box'>  */}
                <li className={'ell-comp'} value={value} onClick={onClick} style={{background_color: Color}}>{answer}</li>
                {/* </label> */}
            </div>
        );
    }

    return (
        <div>

            <h3>{ QnA_5.page }</h3> 
            <h1>{ QnA_5.Question }</h1>
            <div className={'ell-body'}>
                {QnA_5.Answers.map(answer => (
                    <MulSelection key= {answer.id} Answer={answer} />
                ))}
            </div>
            <h4>Answer: { Literacy }</h4>

            {/* <Link to={{
                pathname: "/q6",
                state: {
                    Period: {Period},
                    Purpose: {Purpose},
                    Tolerance: {Tolerance},
                    Literacy: {Literacy},
                    Experience: 0
                }
            }}>
                <NextButton/>
            </Link>    */}

        </div>
    );
}

export default Test;
