import React from "react";
import Radio from "@mui/material/Radio";
import RadioGroup from "@mui/material/RadioGroup";
import FormControlLabel from "@mui/material/FormControlLabel";
import FormControl from "@mui/material/FormControl";
import FormLabel from "@mui/material/FormLabel";

function GBIGraph({ Min, Max, Pro }) {
  //거치식 GBI
  function formula_LumpSum(T, r, t) {
    return T / (r / 100 + 1) ** t;
  }
  //적립식 GBI
  function formula_Mutual(T, r, t) {
    return (T * (r / 100) + 1) / (1 + r / 100) ** t;
  }

  const [Method, setMethod] = React.useState("LumpSum");

  const handleRadio = (event) => {
    setValue(event.target.value);
  };

  return (
    <div>
      {/* 라디오 버튼 */}
      <FormControl>
        <FormLabel id="method">투자 방법</FormLabel>
        <RadioGroup row aria-labelledby="method" name="method">
          <FormControlLabel
            value="LumpSum"
            control={<Radio />}
            label="한번에 투자"
          />
          <FormControlLabel
            value="Mutual"
            control={<Radio />}
            label="적립해 투자"
          />
        </RadioGroup>
      </FormControl>
    </div>
  );
}

export default GBIGraph;
