              </div>
            </div>
            {/* ìºë¦­í„° ì„¤ëª… +  */}
            <div>
              <p>{Res_Type.Content}</p>
            </div>
          </div>
        </div>
        <NextButton Path={"/q7"} Res={Res} Text={"í…ŒìŠ¤íŠ¸ ì´ì–´í•˜ê¸°"} />
        <NextButton
