    ReceiveYears = [...new Array(ReceivePeriod)]
      .map((_, i) => AccumulatePeriod + i + 1)
      .reverse();
  }

  GoalMoney =
    (FV(MonthExpense * 12, AccumulatePeriod + 1) * (1.02 ** ReceivePeriod - 1)) /
    (1.02 - 1);
