  //ì ë¦½ê¸°ê°„ ë°ì´í„° ë§Œë“¤ê¸°
  const AccumulateYears = Array.from(
    { length: parseInt(AccumulatePeriod) + 1 },
    (_, i) => i
  );
  const AccumulateData = AccumulateYears.map(function (t) {
    return RCompoundContribution(P, PMT, t, Pro);
