import React, { useState } from "react";
import {
    ComposedChart,
    XAxis,
    YAxis,
    Tooltip,
    Legend,
    CartesianGrid,
    Area,
    Line,
} from "recharts";
import Box from "@mui/material/Box";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import Select from "@mui/material/Select";

function ProfitGraph({ P, Min, Max, Pro }) {
    //ë‹¨ë¦¬, ë³µë¦¬ => ë…„ ë‹¨ìœ„ë¡œ ê³„ì‚°
    //ì ë¦½ì‹ ë³µë¦¬ : Compound + Contribute
    const Year = [0, 5, 10, 15, 20, 25, 30, 35, 40];
    const Contribution = [0, 100, 200, 300, 400, 500, 600, 700, 800, 900, 1000]; //ì ë¦½ê¸ˆì•¡
    const inflationRate = 2; //ë¬¼ê°€ ìƒìŠ¹ë¥ 
    function cov_Money(Money) {
        let scaledValue = Money;

        if (scaledValue < 10000) {
            scaledValue = Math.round(scaledValue / 10) * 10;
        }

        while (scaledValue >= 10000) {
            scaledValue /= 10000;
        }
        return parseInt(scaledValue);
    }

    function cov_Units(Money) {
        const units = ["ë§Œì›", "ì–µì›"];
        let unitIndex = 0;

        while (Money >= 10000 && unitIndex < units.length - 1) {
            unitIndex += 1;
            Money /= 10000;
        }
        return units[unitIndex];
    }

    const covMoney = cov_Money(P);
    const covUnits = cov_Units(P);
    const [Period, getPeriod] = useState(15);
    const [PMT, getPMT] = useState(0);

    //ì†Œìˆ˜ì  ë‘˜ì§¸ ìë¦¬ ë°˜ì˜¬ë¦¼
    function round_two(float) {
        return Math.round(float * 1e2) / 1e2;
    }
    //ë‹¨ë¦¬ ê³µì‹
    function formula_Simple(r, t) {
        return covMoney + (r / 100) * covMoney * t;
    }
    //ë³µë¦¬ ê³µì‹
    function formula_Compound(r, t) {
        return covMoney * (r / 100 + 1) ** t;
    }
    //ì ë¦½ì‹ ë³µë¦¬ ê³µì‹
    function formula_Contribute(r, t) {
        const pmt = covUnits === "ì–µì›" ? PMT / 10000 : PMT;
        if(r !== 0){
            return (pmt * ((1 + r / 100) ** t - 1)) / (r / 100);
        }
        else { //ì´ììœ¨ì´ 0ì¸ ê²½ìš°, ê³„ì‚°ì‹ ë¶„ëª¨ì— 0ì´ ë“¤ì–´ê°€ë©´ì„œ ê°’ì´ ë¬´í•œëŒ€ë¡œ ìˆ˜ë ´ -> ì˜ˆì™¸ì²˜ë¦¬
            return pmt * t;
        }
    }
    //í˜„ì¬ê°€ì¹˜ ê³µì‹
    function formula_PV(FV, t) {
        return FV / (1 + inflationRate / 100) ** t;
    }

    //ë‹¨ë¦¬ ê³„ì‚°ê¸°
    function Cal_Simple(t) {
        const MIN_VALUE = formula_Simple(Min, t);
        const MAX_VALUE = formula_Simple(Max, t);
        const REV_VALUE = formula_Simple(Pro, t);
        const obj = {
            year: t + "ë…„",
            ì˜ˆìƒìˆ˜ìµë²”ìœ„: [round_two(MIN_VALUE), round_two(MAX_VALUE)],
            ì˜ˆìƒìˆ˜ìµ: round_two(REV_VALUE),
            í˜„ì¬ê°€ì¹˜: round_two(formula_PV(REV_VALUE, t)),
        };
        return obj;
    }

    //ë³µë¦¬ ê³„ì‚°ê¸°
    //*ë…„ë³µë¦¬
    function Cal_Compound(t) {
        const MIN_VALUE = formula_Compound(Min, t);
        const MAX_VALUE = formula_Compound(Max, t);
        const REV_VALUE = formula_Compound(Pro, t);
        const obj = {
            year: t + "ë…„",
            ì˜ˆìƒìˆ˜ìµë²”ìœ„: [round_two(MIN_VALUE), round_two(MAX_VALUE)],
            ì˜ˆìƒìˆ˜ìµ: round_two(REV_VALUE),
            í˜„ì¬ê°€ì¹˜: round_two(formula_PV(REV_VALUE, t)),
        };
        return obj;
    }

    //ì ë¦½ì‹ ë³µë¦¬ ê³„ì‚°ê¸°
    //*ë…„ë³µë¦¬
    function Cal_Compound_Contribution(t) {
        const MIN_VALUE = formula_Compound(Min, t) + formula_Contribute(Min, t);
        const MAX_VALUE = formula_Compound(Max, t) + formula_Contribute(Max, t);
        const REV_VALUE = formula_Compound(Pro, t) + formula_Contribute(Pro, t);
        const obj = {
            year: t + "ë…„",
            ì˜ˆìƒìˆ˜ìµë²”ìœ„: [round_two(MIN_VALUE), round_two(MAX_VALUE)],
            ì˜ˆìƒìˆ˜ìµ: round_two(REV_VALUE),
            í˜„ì¬ê°€ì¹˜: round_two(formula_PV(REV_VALUE, t)),
        };
        return obj;
    }

    //ë°ì´í„° ì„¸íŒ…
    const data = Year.map(function (t) {
        return Cal_Compound_Contribution(t);
    });

    console.log(data);

    //Contribution handleChange
    const CON_handleChange = (event) => {
        getPMT(event.target.value);
    };

    //Period handleChange
    const PER_handleChange = (event) => {
        getPeriod(event.target.value);
    };

    return (
      <div>
        <div style={{ marginTop: "1rem" }}>
          <h3 style={{ margin: "0.3rem 0" }}>ğŸ“ˆ ì˜ˆìƒ ìˆ˜ìµë¥  ê·¸ë˜í”„</h3>
          <ComposedChart
            width={400}
            height={250}
            margin={{ top: 20, bottom: 20 }}
            data={data}
          >
            <XAxis
              dataKey="year"
              tick={{ fontSize: 10 }}
              padding={{ right: 20 }}
            />
            <YAxis
              unit={covUnits}
              tick={{ fontSize: 10 }}
              padding={{ bottom: 10 }}
            />
            <Tooltip />
            <Legend tick={{ fontSize: 10 }} />
            <CartesianGrid stroke="#f4f4f4" />
            <Area
              unit={covUnits}
              type="monotone"
              dataKey="ì˜ˆìƒìˆ˜ìµë²”ìœ„"
              fill="#FFB950"
              stroke="#FFB950"
            />
            <Line
              unit={covUnits}
              type="monotone"
              dataKey="ì˜ˆìƒìˆ˜ìµ"
              stroke="#1D1A82"
            />
            <Line
              unit={covUnits}
              type="monotone"
              dataKey="í˜„ì¬ê°€ì¹˜"
              stroke="#B45CCA"
            />
          </ComposedChart>
          <div className="Discription">
            <span style={{ marginRight: "0.3rem" }}>ë§¤ë…„ </span>
            <Box sx={{ display: "flex" }}>
              <FormControl variant="standard" size="small">
                <Select
                  labelId="contribution-select-label"
                  id="contribution-select"
                  value={PMT}
                  label="ë§Œì›"
                  onChange={CON_handleChange}
                  defaultValue={0}
                >
                  {Contribution.map((Value, index) => {
                    return (
                      <MenuItem key={index} value={Value}>
                        {Value}ë§Œì›
                      </MenuItem>
                    );
                  })}
                </Select>
              </FormControl>
            </Box>
            <span style={{ marginLeft: "0.3rem", marginRight: "0.3rem" }}>
              {" "}
              ì„ ì ë¦½í–ˆì„ ë•Œ{" "}
            </span>
            <Box sx={{ display: "flex" }}>
              <FormControl variant="standard">
                <Select
                  labelId="year-select-label"
                  id="year-select"
                  value={Period}
                  label="ë…„"
                  onChange={PER_handleChange}
                  defaultValue={0}
                >
                  {Year.map((Value, index) => {
                    return (
                      <MenuItem key={index} value={Value}>
                        {Value}ë…„
                      </MenuItem>
                    );
                  })}
                </Select>
              </FormControl>
            </Box>
            <span> ë’¤ì˜ </span>
            <span>
              ì˜ˆìƒ ìˆ˜ìµì€{" "}
              <b>
                {data[Period / 5].ì˜ˆìƒìˆ˜ìµ}
                {covUnits}
              </b>
              ì…ë‹ˆë‹¤.
              <br />
              (í˜„ì¬ ê°€ì¹˜ë¡œ í™˜ì‚°ì‹œ{" "}
              <b>
                {data[Period / 5].í˜„ì¬ê°€ì¹˜}
                {covUnits}
              </b>
              )
            </span>
          </div>
        </div>
      </div>
    );
}

export default ProfitGraph;
