import React from "react";
import "../App.css";
import Header from "../component/Header";
import ASCORE from "../content/ASCORE";
import CLASSIFY from "../content/CLASSIFY";
import CALCULATE from "../content/CALCULATE";
import FINAL_TYPE from "../content/FINAL_TYPE";
import NextButton from "../component/NextButton";
import Graph from "../component/Graph";
import CircularBar from "../component/CircularBar";
import {
  RadarChart,
  PolarGrid,
  PolarAngleAxis,
  PolarRadiusAxis,
  Radar,
} from "recharts";

function Final_Result(props) {
  const Res = props.location.state.Res;
  console.log(Res);

  //íˆ¬ìëª©ì , ìœ„í—˜ê°ë‚´ë„, ê¸ˆìœµì´í•´ë„, íˆ¬ìê²½í—˜ ì¸ë±ìŠ¤ (ë“±ê¸‰, ì ìˆ˜ ì—°ê²°)
  function formula_Index(Score) {
    const Index =
      Score >= 100
        ? 4 - parseInt(99 / 25)
        : Score < 0
        ? 4 - parseInt(1 / 25)
        : 4 - parseInt(Score / 25);
    console.log("**formula_Index", Score, Index);

    return Index;
  }

  //Type: PERIOD, PURPOSE, TOLERANCE, LITERACY, EXPERIENCE
  function find_ResultData(Type, Index) {
    console.log("**find_ResultData", Type, Index);
    return CALCULATE[Type][
      CALCULATE[Type].findIndex((data) => data.Index === Index)
    ];
  }

  //ê° ë¬¸í•­ ì ìˆ˜ ë°›ì•„ì˜¤ê¸° (ë“±ê¸‰ êµ¬í•˜ëŠ” ë¬¸í•­ ì œì™¸í•˜ê³ ëŠ” ì¸ë±ìŠ¤ë¡œ ì„¸íŒ…)
  function getScore(questionID) {
    const Tdata = ASCORE[ASCORE.findIndex((data) => data.id === questionID)];
    if (Tdata.Type === "Single") {
      return Tdata.Answer[
        Tdata.Answer.findIndex((d) => d.id === Res[questionID])
      ].Score;
    } else if (Tdata.Type === "Multi") {
      let Score = 0;

      for (let i = 0; i < Res[questionID].length; i++) {
        Score =
          Score +
          Tdata.Answer[
            Tdata.Answer.findIndex((d) => d.id === Res[questionID][i])
          ].Score;
      }
      return Score;
    }
  }

  const PERIOD = find_ResultData("PERIOD", getScore(1));
  const PURPOSE = find_ResultData(
    "PURPOSE",
    formula_Index((getScore(2) + getScore(7)) / 2)
  );
  const TOLERANCE = find_ResultData(
    "TOLERANCE",
    formula_Index(getScore(3) + getScore(4) + getScore(8))
  );
  const LITERACY = find_ResultData(
    "LITERACY",
    formula_Index(getScore(5) + getScore(10) + getScore(11))
  );
  const EXPERIENCE = find_ResultData(
    "EXPERIENCE",
    formula_Index((getScore(6) + getScore(13)) / 2)
  );
  const AGE = find_ResultData("AGE", getScore(14));
  const INCOME = find_ResultData("INCOME", getScore(15));
  const PROPERTY = find_ResultData("PROPERTY", getScore(17));
  // ë³´ìœ íš¨ê³¼
  const STATUSQUO = find_ResultData("STATUSQUO", getScore(12));
  // ë³´ìœ íš¨ê³¼
  const ENDOWMENT = find_ResultData("ENDOWMENT", getScore(9));
  // ì†ì‹¤íšŒí”¼
  let LOSSAVERSION = -1;
  if (TOLERANCE.Grade === 1) {
    LOSSAVERSION = find_ResultData("LOSSAVERSION", 4);
  } else {
    for (let i = 0; i < CALCULATE.LOSSAVERSION.length; i++) {
      if (
        CALCULATE.LOSSAVERSION[i].Range.includes(
          PURPOSE.Grade - TOLERANCE.Grade
        )
      )
        LOSSAVERSION = find_ResultData("LOSSAVERSION", i + 1);
    }
  }
  // ì„±ê³¼ì¶”ì¢…
  let PERFORMANCE = -1;
  for (let i = 0; i < CALCULATE.PERFORMANCE.length; i++) {
    if (CALCULATE.PERFORMANCE[i].Range.includes(getScore(9) - Res[4]))
      PERFORMANCE = find_ResultData("PERFORMANCE", i + 1);
  }
  // ìê¸°ê³¼ì‹ 
  const OVERCONFIDENCE = find_ResultData("OVERCONFIDENCE", getScore(12));

  const BEHAVIOR = [
    { column: "í˜„ìƒìœ ì§€", value: 5 - STATUSQUO.Grade, fullMark: 5 },
    { column: "ë³´ìœ íš¨ê³¼", value: 5 - ENDOWMENT.Grade, fullMark: 5 },
    { column: "ì†ì‹¤íšŒí”¼", value: 5 - LOSSAVERSION.Grade, fullMark: 5 },
    { column: "ì„±ê³¼ì¶”ì¢…", value: 5 - PERFORMANCE.Grade, fullMark: 5 },
    { column: "ìê¸°ì£¼ê´€", value: 5 - OVERCONFIDENCE.Grade, fullMark: 5 },
  ];
  console.log(BEHAVIOR);
  const tick = [0, 1, 2, 3, 4];
  const formatTick = (tickItem) => {
    // if (tickItem)
    // console.log(tickItem)
    if (tickItem === 0) return "";
    else return `${5-tickItem}`
  }

  //í† íƒˆ ì ìˆ˜ ê³„ì‚°
  const Score =
    PURPOSE.Value +
    TOLERANCE.Value +
    LITERACY.Value +
    EXPERIENCE.Value +
    PERIOD.Value +
    AGE.Value +
    INCOME.Value +
    PROPERTY.Value;

  console.log("íˆ¬ìì„±í–¥ì ìˆ˜",Score);

  const Class = CLASSIFY[CLASSIFY.findIndex((c) => c.id === 2)];
  function SetType(Score, PURPOSE, TOLERANCE) {
    const Classify =
      Class.ê³µê²©íˆ¬ìí˜•[0] <= Score && Score <= Class.ê³µê²©íˆ¬ìí˜•[1]
        ? "ê³µê²©íˆ¬ì"
        : Class.ì ê·¹íˆ¬ìí˜•[0] <= Score && Score <= Class.ì ê·¹íˆ¬ìí˜•[1]
        ? "ì ê·¹íˆ¬ì"
        : Class.ìœ„í—˜ì¤‘ë¦½í˜•[0] <= Score && Score <= Class.ìœ„í—˜ì¤‘ë¦½í˜•[1]
        ? "ìœ„í—˜ì¤‘ë¦½"
        : Class.ì•ˆì •ì¶”êµ¬í˜•[0] <= Score && Score <= Class.ì•ˆì •ì¶”êµ¬í˜•[1]
        ? "ì•ˆì •ì¶”êµ¬"
        : Class.ì•ˆì •í˜•[0] <= Score && Score <= Class.ì•ˆì •í˜•[1]
        ? "ì•ˆì •í˜•"
        : console.log("No Result");

    const TypeNum =
      Classify === "ì•ˆì •í˜•"
        ? ""
        : TOLERANCE.Grade === 1 || PURPOSE > TOLERANCE
        ? "Type1 "
        : PURPOSE === TOLERANCE
        ? "Type2 "
        : PURPOSE < TOLERANCE
        ? "Type3 "
        : console.log("Type ì²´í¬ ì—ëŸ¬");
    const Char = Classify + " " + TypeNum + "ìºë¦­í„°";
    return Char;
  }

  const Type =
    FINAL_TYPE[
      FINAL_TYPE.findIndex(
        (type) => type.Char === SetType(Score, PURPOSE.Grade, TOLERANCE.Grade)
      )
    ];
  const MONEY = Res[16];
  const MIN = Type.Min;
  const MAX = Type.Max;
  const REV = Type.Revenue;

  return (
    <div className="App">
      <Header history={props.history} />
      <div className="Result">
        <div>
          <div>
            <div className="Quest">
              <div
                style={{
                  fontSize: "20px",
                  fontWeight: "700",
                  fontFamily: "DungGeunMo",
                }}
              >
                Quest
              </div>
              <div>{PERIOD.Quest}</div>
            </div>
            {/* <img className='TypeImage'></img> */}
            <div className="TypeImage"></div>
            <h1
              style={{
                color: "#fefefe",
                fontFamily: "DungGeunMo",
                padding: "0.5rem",
              }}
            >
              {Type.Char}
            </h1>
          </div>

          <div className="Explain">
            <h3 style={{ margin: "0", paddingBottom: "0.8rem" }}>
              ğŸ˜ ë‹¹ì‹ ì˜ ìºë¦­í„°ëŠ”...
            </h3>
            <div className="CharInfo">
              {/* ì²«ë²ˆì§¸ ì¹¼ëŸ¼: ë“±ê¸‰í‘œ */}
              <div className="Table">
                <CircularBar Type="íˆ¬ì ëª©ì " Grade={PURPOSE.Grade} />
                <CircularBar Type="ìœ„í—˜ ê°ë‚´ë„" Grade={TOLERANCE.Grade} />
                <CircularBar Type="ê¸ˆìœµ ì´í•´ë„" Grade={LITERACY.Grade} />
                <CircularBar Type="íˆ¬ì ê²½í—˜" Grade={EXPERIENCE.Grade} />
              </div>
              {/* 2ë²ˆì§¸ ì¹¼ëŸ¼: ì•„ì´í…œ */}
              <div className="Bag">
                <div className="Items">
                  <div className="ItemImg">
                    <img
                      width={42}
                      height={42}
                      src={LITERACY.Img}
                      alt="ë¬´ê¸° ì´ë¯¸ì§€"
                    />
                  </div>
                  <div
                    style={{
                      marginTop: "auto",
                      marginBottom: "auto",
                      marginLeft: "0.5rem",
                    }}
                  >
                    <b>{LITERACY.Weapon}</b> <br />
                    {LITERACY.Level}
                  </div>
                </div>
                <div className="Items">
                  <div className="ItemImg">
                    <img
                      width={42}
                      height={42}
                      src={TOLERANCE.Img}
                      alt="ë°©íŒ¨ ì´ë¯¸ì§€"
                    />
                  </div>
                  <div
                    style={{
                      marginTop: "auto",
                      marginBottom: "auto",
                      marginLeft: "0.5rem",
                    }}
                  >
                    <b>{TOLERANCE.Shield}</b> <br />
                    {TOLERANCE.Level}
                  </div>
                </div>
              </div>
            </div>
            {/* ìºë¦­í„° ì„¤ëª… */}
            <div>
              <p style={{ lineHeight: "1.5rem" }}>{Type.Content}</p>
            </div>
            <h3 style={{ margin: "0", paddingBottom: "0.8rem" }}>
              ğŸ¤” ìºë¦­í„°ì˜ íŠ¹ì§•ì€...
            </h3>
            <div style={{ display: "flex", alignItems: "center" }}>
              <RadarChart
                outerRadius={60}
                width={260}
                height={160}
                // style={{ margin: "0 auto" }}
                style={{ display: "flex" }}
                data={BEHAVIOR}
              >
                <PolarGrid />
                <PolarAngleAxis dataKey="column" style={{ fontSize: "14px" }} />
                <PolarRadiusAxis
                  angle={90}
                  // domain={[0, 5]}
                  ticks={tick}
                  tickFormatter={formatTick}
                  style={{ fontSize: "12px" }}
                />
                <Radar
                  name=""
                  dataKey="value"
