import React, { useState } from 'react';
import '../App.css';
import Header from '../component/Header';
import NextButton from '../component/NextButton';
import QNA from '../content/QNA'
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Slider from '@mui/material/Slider';

const QnA = QNA[16];

function Q17(props) {
  const Res = props.location.state.Res;
  console.log(Res);

  const [Response, getResponse] = useState('Not Selected!');
  const [Sel, setSel] = useState(false);

  function SelButton({ Answer }) {
    const id = Answer.id;
    const answer = Answer.Answer;

    const onClick = () => {
      QnA.Answers.map(answer => (
          answer.State = false
      ));
      Answer.State = !Answer.State;
      setSel(true);
      console.log(answer);
      getResponse(id);
    }
    return (
      <div>
        <button className={'sel-button'} onClick={ onClick } style={{backgroundColor: Answer.State ? '#1d1a82':'#F7F7F7', color: Answer.State && 'White'}}>{answer}</button>
      </div>
    );
  }

  Res[QnA.page] = Response;

  const marks = [
    {
      value: 0,
      label: '1만원'
    },
    {
      value: 20,
      label: '100만원'
    },
    {
      value: 100,
      label: '10억 이상'
    }
  ];

  function valuetext(value) {
    return '${value}만원'
  };

  return (
    <div className="App">
      <Header />
      <div className='Content'>
        <h3 className='page'>{ QnA.page }</h3> 
        <h1 className='question'> { QnA.Question }</h1>
        <div style={{width: '24rem', margin: '3rem', display:'inline-flex'}}>
          <Slider
              aria-label="Investment Amount"
              defaultValue={100}
              // valueLabelFormat={auto}
              getAriaValueText={valuetext}
              step={null}
              valueLabelDisplay="auto"
              marks={marks}
          />
        </div>
        <NextButton Path={"/q18"} Res={Res} Text={'Next'}/>
      </div>
    </div>
  );
}

export default Q17;
