import React, { useState } from "react";
import FormControl from "@mui/material/FormControl";
import {
  Box,
  InputAdornment,
  Input,
  MenuItem,
  Select
} from "@mui/material";

function GBIGraph({ Min, Max, Pro }) {
  //거치식 GBI
  function formula_LumpSum(T, r, t) {
    return T / (r / 100 + 1) ** t;
  }
  //적립식 GBI
  function formula_Mutual(T, r, t) {
    return (T * (r / 100) + 1) / (1 + r / 100) ** t;
  }

  const [Method, setMethod] = useState("LumpSum");
  const [Period, setPeriod] = useState(1);
  const [Money, setMoney] = useState(100);

  const handleMethod = (event) => {
    setMethod(event.target.value);
  };
  const handlePeriod = (event) => {
    setPeriod(event.target.value);
  };
  const handleMoney = (event) => {
    setMoney(event.target.value);
  };

  return (
    <div>
      <div className="Discription">
        <Box sx={{ display: "flex" }}>
          <FormControl variant="standard">
            <Select
              labelId="year-select-label"
              id="year-select"
              value={Method}
              onChange={handleMethod}
              defaultValue={"거치식"}
            >
              <MenuItem value="LumpSum">거치식</MenuItem>
              <MenuItem value="Mutual">적립식</MenuItem>
            </Select>
          </FormControl>
        </Box>
        <span>으로</span>
        <Box sx={{ display: "flex" }}>
          <FormControl variant="standard">
            <Input
              id="period"
              value={Period}
              onChange={handlePeriod}
              endAdornment={<InputAdornment position="end">년</InputAdornment>}
              size="small"
              sx={{ width: "48px", ml: "0.5rem" }}
            />
          </FormControl>
        </Box>
        <span>동안</span>
        <Box sx={{ display: "flex" }}>
          <FormControl variant="standard">
            <Input
              id="money"
              value={Money}
              onChange={handleMoney}
              endAdornment={
                <InputAdornment position="end">만원</InputAdornment>
              }
              inputProps={{ pattern: "[0-9]*" }}
              //   type="number"
              size="small"
              sx={{ width: "120px", textAlign: "right", ml: "0.5rem" }}
            />
          </FormControl>
        </Box>
        <span>을</span>
        <span>모으기 위해...</span>
      </div>
    </div>
  );
}

export default GBIGraph;
