import React, { useState } from 'react';
import '../App.css';
import Header from '../component/Header';
import NextButton from '../component/NextButton';
import QNA from '../content/QNA'
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Slider from '@mui/material/Slider';

const QnA = QNA[16];

function Q17(props) {
  const Res = props.location.state.Res;
  // console.log(Res);

  const [Response, getResponse] = useState(10);
  const [Sel, setSel] = useState(false);

  function SelButton({ Answer }) {
    const id = Answer.id;
    const answer = Answer.Answer;

    const onClick = () => {
      QnA.Answers.map(answer => (
          answer.State = false
      ));
      Answer.State = !Answer.State;
      setSel(true);
      console.log(answer);
      getResponse(id);
    }
    return (
      <div>
        <button className={'sel-button'} onClick={ onClick } style={{backgroundColor: Answer.State ? '#1d1a82':'#F7F7F7', color: Answer.State && 'White'}}>{answer}</button>
      </div>
    );
  }

  Res[QnA.page] = Response;

  function valueLabelFormat(Response) {
    const units = ['만원', '억원'];

    let unitIndex = 0;
    let scaledValue = Response;

    while (scaledValue >= 10000 && unitIndex < units.length - 1) {
      unitIndex += 1;
      scaledValue /= 10000;
    }

    return `${parseInt(scaledValue)} ${units[unitIndex]}`;
  }

  function calculateValue(Response) {
    // return 2 ** Response;
    // return Response;
    // return Response * 10;
    console.log(Math.log10(Response));
    return 10 ** (Math.log10(Response)*(2));
  }

  const handleChange = (event, newValue) => {
    if (typeof newValue === 'number') {
      getResponse(newValue);
    }
  };

  return (
    <div className="App">
      <Header />
      <div className='Content'>
        <h3 className='page'>{ QnA.page }</h3> 
        <h1 className='question'> { QnA.Question }</h1>
        {/* <div style={{width: '24rem', margin: '3rem', display:'inline-flex'}}> */}
        <Box sx={{ width: 300, display: 'inline-block' }}>
          <Typography id="non-linear-slider" gutterBottom>
            투자 금액: {valueLabelFormat(calculateValue(Response))}
          </Typography>
          <Slider
            value={Response}
            min={1}
            step={1}
            max={100}
            scale={calculateValue}
            getAriaValueText={valueLabelFormat}
            valueLabelFormat={valueLabelFormat}
            onChange={handleChange}
            valueLabelDisplay="auto"
            aria-labelledby="non-linear-slider"
          />
        </Box>
        <NextButton Path={"/q18"} Res={Res} Text={'Next'}/>
      </div>
    </div>
  );
}

export default Q17;
